var audioPlayer=document.getElementById("audiofile");const subtitles=document.getElementById("subtitles").querySelectorAll("span"),legendas=document.getElementById("legendas").querySelectorAll("span"),sync=[];function updateCurrentTime(a){audioPlayer.currentTime=a,audioPlayer.play()}subtitles.forEach(a=>{const b=a.getAttribute("data-start"),c=a.getAttribute("data-end");sync.push({start:b,end:c}),a.ondblclick=function(){updateCurrentTime(b)}}),legendas.forEach(a=>{const b=a.getAttribute("data-start");a.ondblclick=function(){updateCurrentTime(b)}}),audioPlayer.addEventListener("timeupdate",function(){document.querySelectorAll("span.active-caption").forEach(a=>{a.classList.remove("active-caption")});const a=sync.findIndex(a=>audioPlayer.currentTime>=a.start&&audioPlayer.currentTime<=a.end);try{subtitles[a].classList.add("active-caption"),legendas[a].classList.add("active-caption")}catch{}})